       (7856, 677, '_tabs_1_rows_4_youtube_link', 'field_60a9188239912'),
       (7857, 677, 'tabs_1_rows_4_text', 'Jetzt auch einmal die Baustelle aus der Vogelperspektive betrachten.'),
