       (7872, 677, '_tabs_1_rows_6_pretitle', 'field_60ad177ba9912'),
       (7873, 677, 'tabs_1_rows_6_title', 'Fertigstellung \"ROHBAU UND DACH\"!'),
       (7874, 677, '_tabs_1_rows_6_title', 'field_60ad177bae083'),
