       (7876, 677, '_tabs_1_rows_6_youtube_link', 'field_60a9188239912'),
       (7877, 677, 'tabs_1_rows_6_text',
        'Die ersten beiden Wohnhäuser sind schon eingedeckt und bei unseren Reihenhäusern steht der erste Stock! An den Installationen und der Elektrik wird parallel auch fleißig gearbeitet. Wir freuen uns dass alles nach Plan läuft!'),
       (7878, 677, '_tabs_1_rows_6_text', 'field_60ad178fae084'),
       (7879, 677, 'tabs_1_rows_7_picture', '720'),
