       (13702, 954, 'services_one_service_points_0_text',
        'Wir ermitteln den optimalen Marktpreis für Ihre Immobilie und erstellen alle Verkaufsunterlagen.\r\n'),
       (13703, 954, '_services_one_service_points_0_text', 'field_60a155c7f1f3a'),
